import React from 'react'
import DataTable from '../components/DataTable'

const departments = ["Engineering", "Administration", "HR"]
const columns = [
    {
        name: "Rule Name",
        field: "name",
        type: "text"
    },
    {
        name: "Label",
        field: "label",
        type: "text"
    },
    {
        name: "Status",
        field: "status",
        type: "select",
        options: ["Enabled", "Disabled"]
    }, 
    {
        name: "DLP Engine",
        field: "dlpEngine",
        type: "text"
    },
    {
        name: "File Type",
        field: "fileType",
        type: "text"
    },
    {
        name: "Department",
        field: "department",
        type: "select",
        options: departments
    }
]


const DLPPolicy = () => {
    return(
        <DataTable columns={columns} url={"dlp"} />
    )
}

export default DLPPolicy
